const getInfoByToken = async () => {
    const opts = {
        credentials: "include",
        headers: {
        Authorization: `Bearer ${localStorage.getItem('access_token')}`,
        },
    }
    let res = await fetch('http://127.0.0.1:5000/@me', opts)
    return await res.json()
}



export default getInfoByToken
