import React, {useState} from 'react'
import { sendData } from '../fetch'
import NewInput from '../Components/UI/NewInput/NewInput'

const LogIn = () => {
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    const [text, setText] = useState("")

    return (
        <div>
        <form>
            <NewInput placeholder="email" value={email} type="text" placeholder='email' onChange={(e) => setEmail(e.target.value)}/>
            <NewInput placeholder="password" value={password} type="password"  onChange={(e) => setPassword(e.target.value)}/>
            <NewInput text="LogIn" type="button" value="LogIn" onClick={() => {
                let res = sendData("http://127.0.0.1:5000/login", {email, password})
                setPassword("")
                setEmail("")
                setText(res.access_token)
                }}/>        
        </form>
        <h3>{text}</h3>
        </div>
  )
}

export default LogIn
