import React, {useState} from 'react'

const LogIn = () => {
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    const [text, setText] = useState("")
    

    const sendData = async (url, data) => {
        const opts = {
            credentials: "include",
            method: "POST",
            headers: {
                Authorization: `Bearer ${localStorage.getItem('access')}`,
              },
            body: JSON.stringify(data)
        }       
        const response = await fetch(url, opts)
        let res = await response.json();
        if (response.status === 200) {
            setEmail("")
            setPassword("")
            setText(res.access_token)
            res.access_token ? localStorage.setItem("access_token", res.access_token): console.log(res)
        }
    }


    return (
        <div>
        <form>
            <input value={email} type="text" placeholder='email' onChange={(e) => setEmail(e.target.value)}></input>
            <input value={password} type="password" placeholder='password' onChange={(e) => setPassword(e.target.value)}></input>
            <input type="button" value="LogIn" onClick={() => sendData("http://127.0.0.1:5000/login", {email, password})}></input>            
        </form>
        <h3>{text}</h3>
        </div>
  )
}

export default LogIn
