import React, { useState } from 'react'
import NewInput from '../Components/UI/NewInput/NewInput'
import { sendData } from '../fetch'


const Settings = () => {
    const [nickname, setNickname] = useState("")
    return (
        <div>
            <div>
                <h3>Change nickname</h3>
                <NewInput placeholder="New nickname" type='input' value={nickname} onChange={(e) => setNickname(e.target.value)}/>
                <NewInput text='Submit' type='button' onClick={async() => sendData('http://127.0.0.1:5000/update_info', {nickname}, 'update')}/>
            </div>
        </div>
    )
}

export default Settings
