import React, {useState} from 'react'
import { sendData } from '../fetch'

const LogIn = () => {
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    const [text, setText] = useState("")
    let answer = sendData('http://127.0.0.1:5000/login', {email, password})

    
    if (answer.access_token) {
        setEmail("")
        setPassword("")
        setText(answer.access_token)
        localStorage.setItem("access_token", answer.access_token)
    }


    return (
        <div>
        <form>
            <input value={email} type="text" placeholder='email' onChange={(e) => setEmail(e.target.value)}></input>
            <input value={password} type="password" placeholder='password' onChange={(e) => setPassword(e.target.value)}></input>
            <input type="button" value="LogIn" onClick={() => sendData("http://127.0.0.1:5000/login", {email, password})}></input>            
        </form>
        <h3>{text}</h3>
        </div>
  )
}

export default LogIn
