import React, {useState, useEffect} from 'react'

const LogIn = () => {
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    const [text, setText] = useState("")
    const [cookie, setCookie] = useState('');

    useEffect(() => {
        setCookie(document.cookie); // Получение значения всех куков
    }, []);

    const sendData = async (url, data) => {
        const opts = {
            credentials: "include",
            method: "POST",
            headers: {
            "Content-Type": "application/json"
            },
            body: JSON.stringify(data)
        }       
        const response = await fetch(url, opts)
        let res = await response.json();
        console.log(res)
        console.log(response.status)
        if (response.status === 200) {
            response.headers.forEach(element => {
                console.log(element)
            });
            setEmail("")
            setPassword("")
            setText(res.access_token)
        }
    }

    const get_cookies = () => {
        setCookie(document.cookie)
        console.log(cookie)
    }

    return (
        <div>
        <form>
            <input value={email} type="text" placeholder='email' onChange={(e) => setEmail(e.target.value)}></input>
            <input value={password} type="password" placeholder='password' onChange={(e) => setPassword(e.target.value)}></input>
            <input type="button" value="LogIn" onClick={() => sendData("http://127.0.0.1:5000/login", {email, password})}></input>            
        </form>
        <h3>{text}</h3>
        <input type="button" value="click" onClick={get_cookies}></input>
        </div>
  )
}

export default LogIn
