import React, {useState, useEffect} from 'react'
import { getDataByToken } from '../fetch'
import PostView from '../Components/UI/PostView/PostView'

const Feed = () => {
  const [posts, setPosts] = useState([])

  const getPosts = () => {
    getDataByToken("http://127.0.0.1:5000/get_all_posts").then(result => {
      if (result.status === 200) {
        setPosts(result.data)
      }
    })
  }

  useEffect(() => {
    getPosts()
  }, [])

  return (
    <div>
        {posts ? posts.map(post => <PostView content={post[0]} date={post[2]} key={post[0]} advancedInfo={post[0]}/>): []}
    </div>
  )
}

export default Feed
